int main(){
    Player Jugador;
    Cube Cubo;
    int exit = 0;
    char opcion, salir = 0, letra;

    PlaySound(TEXT("Theme.wav"), NULL, SND_FILENAME | SND_ASYNC);
    while (exit != 4){
        salir = '0';
        system("cls");
        SetColor(15);
        Principal();
        opcion = getche();
        switch (opcion){
            case '1':
                PlaySound(NULL, NULL, SND_ASYNC);
                system("cls");
                Map();
                Jugador.Pintar(99,3);
                Cubo.objetivo();
                while (salir !=3){
                    letra = getche();
                    Jugador.Movimiento(letra);
                }
            break;
            case '2':
                system("cls");
                while (salir != '3'){
                    Instrucciones();
                    salir = getche();
                }
            break;
            case '3':
                system("cls");
                while (salir != '3'){
                    Creditos();
                    salir = getche();
                }
            break;
            case '4':
                system("cls");
                return 0;
            break;
        }
    }
    return 0;
}
